import React from 'react';
import { useState } from 'react';
import backgroundFrame from './assets/images/backgroundFrame.png';
import wool from './assets/images/wool2.png';
import Question from './components/Question/question.js';

/*
const myButton = document.getElementById("myButton");

myButton.addEventListener("click", function() {
  console.log(myButton.value);
});
*/


function App() {
  const [currentStep, setStep] = useState(1);

  const [cats, setCats] = useState([
    {"name": "Blacky", "likes other cats": "yes", "likes knees": "yes"},
    {"name": "Missy", "likes ther cats": "no", "likes knees": "yes"},
    {"name": "Jay", "likes other cats": "yes", "likes knees": "no"}
  ]);

  function handleNextStep(event) {
    setStep(currentStep + 1);
    console.log(event.target.id)
  }

  function handlePreviousStep() {
    setStep(currentStep - 1);
  }

  //IN PROGRESS!!! 
 cats.forEach(cat => {
  const values = Object.values(cat);
  //console.log(values[currentStep-1])
  //console.log(currentStep)
 })

 function filterCats(cats, questionNumber, userAnswer) { //questionNumber powyżej, za userAnswer podstaw id buttona
  if(userAnswer === "yes"){
    return cats.filter(cat => cat[questionNumber] === "yes");
  } else if(userAnswer === "no"){
    return cats.filter(cat => cat[questionNumber] === "no");
  } else{
    return cats;
  }
 }



  return (
    <div className="App"

      style={{
        backgroundImage: `url(${backgroundFrame})`,
        backgroundRepeat: "no-repeat",
        backgroundSize: "contain",
        width: "100vw",
        height: "100vh",
        backgroundPosition: "center",
        position: "absolute",
        zIndex: "-1"
      }}>



      {currentStep === 1 && (
        <Question
          userQuestion="Czy kot Twoich marzeń lubi inne koty?"
          userAnswer1="Tak!!!"
          userAnswer2="Jest mi to obojętne."
          handleNextStep={handleNextStep}
        />
      )}

      {currentStep === 2 && (
        <Question
          userQuestion="Kot Twoich marzeń to nakolannik czy samotnik?"
          userAnswer1="Miziakowy nakolannik."
          userAnswer2="Samowystarczalny samotnik."
          handleNextStep={handleNextStep}
        />
      )}


      {currentStep === 3 && (
        <Question
          userQuestion="Aktywność kota Twoich marzeń to:"
          userAnswer1="Wulkan energii!!!"
          userAnswer2="Spokojny kanapowiec."
          handleNextStep={handleNextStep}
        />
      )}



      <button className="wool" onClick={handlePreviousStep}><img src={wool} /></button>


    </div>

  );
}

export default App;
