import React, { useState, useEffect } from "react";
import { Swiper, SwiperSlide } from "swiper/react";
import "./catSwiper.scss";
import '../../../node_modules/swiper/swiper.scss';
import { EffectCards, Pagination } from "swiper";
import "swiper/css/pagination";


function CatSwiper(props) {

    const answers = props.array2;
    console.log(answers);

    const [oldAnswers, setOldAnswers] = useState[answers];

   // useEffect(() => { }, [oldAnswers]);

    const handleChange = (event, index) => {
        const newAnswers = [...oldAnswers];
        newAnswers[index] = event.target.value;
        setOldAnswers(newAnswers);
    }

   /* if (props.array1.length == 0) {
        return (


            <div className="no-cats-alert">
                <div>Ups! Obecnie nie mamy kotów, które spełniałyby Twoje
                    potrzeby! :( Jeśli chcesz, możesz wrócić później - nowi podopieczni stale do nas przybywają! -
                    lub spróbować zmienić niektóre odpowiedzi. Czasem kot, który lubi inne koty,
                    może być również wspaniałym jedynakiem - a wiek często nie ma znaczenia ;).
                </div>
                <div>
                    <form>
                        <fieldset>
                            <div>
                                <input type="radio" id="doeslikecats1" name="likes other cats" value="yes" checked={oldAnswers[0] === "yes"}
                                    onChange={(event) => handleChange(event, 0)} />
                                <label for="doeslikecats1">Lubi koty</label>

                                <input type="radio" id="doeslikecats1" name="likes other cats" value="no" checked={oldAnswers[0] === "no"}
                                    onChange={(event) => handleChange(event, 0)} />
                                <label for="doeslikecats1">Nie lubi kotów</label>
                            </div>
                        </fieldset>
                        <fieldset>
                            <div>
                                <input type="radio" id="attitude1" name="likes cuddles" value="yes" checked={oldAnswers[1] === "yes"}
                                    onChange={(event) => handleChange(event, 1)} />
                                <label for="attitude1">Miziak</label>

                                <input type="radio" id="attitude2" name="likes cuddles" value="no" checked={oldAnswers[1] === "no"}
                                    onChange={(event) => handleChange(event, 1)} />
                                <label for="attitude2">Samotnik</label>
                            </div>
                        </fieldset>
                        <fieldset>
                            <div>
                                <input type="radio" id="character1" name="is energetic" value="yes" checked={oldAnswers[2] === "yes"}
                                    onChange={(event) => handleChange(event, 2)} />
                                <label for="character1">Energiczny</label>

                                <input type="radio" id="character2" name="is energetic" value="no" checked={oldAnswers[2] === "no"}
                                    onChange={(event) => handleChange(event, 2)} />
                                <label for="character2">Spokojny</label>
                            </div>
                        </fieldset>
                        <fieldset>
                            <div>
                                <input type="radio" id="isyoung1" name="is young" value="yes" checked={oldAnswers[3] === "yes"}
                                    onChange={(event) => handleChange(event, 3)} />
                                <label for="isyoung1">Młodszy</label>

                                <input type="radio" id="isyoung2" name="is young" value="no" checked={oldAnswers[3] === "no"}
                                    onChange={(event) => handleChange(event, 3)} />
                                <label for="isyoung2">Starszy</label>
                            </div>
                        </fieldset>
                        <fieldset>
                            <div>
                                <input type="radio" id="doeslikedogs1" name="likes dogs" value="yes" checked={oldAnswers[4] === "yes"}
                                    onChange={(event) => handleChange(event, 4)} />
                                <label for="doeslikedogs1">Lubi psy</label>

                                <input type="radio" id="doeslikedogs2" name="likes dogs" value="no" checked={oldAnswers[4] === "no"}
                                    onChange={(event) => handleChange(event, 4)} />
                                <label for="doeslikedogs2">Nie lubi psów</label>
                            </div>
                        </fieldset>
                        <fieldset>
                            <div>
                                <input type="radio" id="ishealthy1" name="is healthy" value="yes" checked={oldAnswers[5] === "yes"}
                                    onChange={(event) => handleChange(event, 5)} />
                                <label for="ishealthy1">Zdrowy</label>

                                <input type="radio" id="ishealthy2" name="is healthy" value="no" checked={oldAnswers[5] === "no"}
                                    onChange={(event) => handleChange(event, 5)} />
                                <label for="ishealthy2">Chory</label>
                            </div>
                        </fieldset>
                        <div>
                            <button type="submit">Spróbuj szczęścia!</button>
                        </div>
                    </form>
                </div>
            </div>
        )
    }

    else*/ {
        return (
            <div>
                <Swiper
                    pagination={{
                        type: "progressbar"
                    }}
                    navigation={true}
                    effect={"cards"}
                    grabCursor={true}
                    modules={[EffectCards, Pagination]}
                    className="mySwiper"
                >

                    {props.array1.map((obj, index) => (
                        <SwiperSlide key={index}>
                            <div className="swiper-content">
                                <div className="cat-swiper-name">{obj.name}</div>
                                <div className="cat-swiper-img"><img src={obj.img}></img></div>
                                <div className="cat-swiper-url"><a href={obj.url}>Zobacz!</a></div>
                            </div>
                        </SwiperSlide>
                    ))}
                </Swiper>
            </div>
        );
    }
}

export default CatSwiper;
